@import url("https://fonts.googleapis.com/css2?family=Roboto+Condensed:wght@300;400;700&display=swap");

html {
  scroll-behavior: smooth;
}
/*CSS Variables*/
$main-color: #2e4c7e;
$main-color-rgba: rgba(46, 76, 126, 0.5);
$main-color-rgba-light: rgba(46, 76, 126, 0.2);
$tint-color: #e2a031;
$secondary-color: #647db3;
$off-white: #e1f0fe;
$white: #ffffff;
$main-font: "Roboto Condensed", sans-serif;
$bg-color: #eee;
$off-black: rgba(0, 0, 0, 0.2);

* {
  box-sizing: border-box;
  margin: 0;
  padding: 0;
}

body {
  font-family: $main-font;
  margin: 0;
  height: auto;
  color: $secondary-color;
  font-size: 16px;
  background: $main-color;
}

.container {
  display: grid;
  grid-template-columns: 1fr 1fr;
  text-align: center;
}

.left,
.right {
  background: $main-color;
  min-height: 100vh;
}

.right {
  width: 40vw;
}

.right p {
  color: $secondary-color;
  font-size: 1.25em;
  text-transform: uppercase;
  margin-top: -20px;
  margin-bottom: 20px;
}

.about-social {
  margin-top: 30px;
  display: flex;
  text-align: center;
  place-items: center;
  width: 100%;
  justify-content: space-around;
}

.portfolio {
  margin-top: 30px;
}

.btn {
  padding: 0.5rem 0.5rem;
  border-right: 1px solid $tint-color;
  border-left: 1px solid $tint-color;
  display: inline-block;
  margin: 1rem 1rem;
  color: $secondary-color;
  text-decoration: none;
  font-size: 1.2rem;
  font-weight: 400;
  background: linear-gradient(to right, $tint-color 50%, $main-color 50%);
  background-size: 200% 100%;
  background-position: right bottom;
  -webkit-animation: swing-in-top-fwd 2s cubic-bezier(0.175, 0.885, 0.32, 1.275)
    both;
  animation: swing-in-top-fwd 2s cubic-bezier(0.175, 0.885, 0.32, 1.275) both;
  transition: all 500ms ease;
}

.btn:hover {
  background-position: left bottom;
  opacity: 0.9;
  color: $main-color;
  cursor: pointer;
}

.fab {
  font-size: 30px;
  color: $secondary-color;
  transition: all 500ms;
}

.fa:hover,
.fab:hover {
  color: $tint-color;
  cursor: pointer;
}

#profile-pic {
  border-radius: 100%;
  border: 5px solid $tint-color;
  height: 40vh;
}

#logo {
  height: 40vh;
  margin-top: -30px;
}

.left {
  display: flex;
  flex-direction: column;
  width: 60vw;
}

.item,
.menu {
  height: 7vh;
  color: $main-color;
  font-size: 1.5rem;
  border-bottom: 4px solid $main-color;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: flex-start;
  background: linear-gradient(to bottom, $secondary-color 50%, $tint-color 50%);
  background-size: 100% 205%;
  background-position: top left;
  transition: all 300ms ease-in-out;
  overflow: hidden;
  padding: 0 15px;
}

.item:hover,
.menu:hover {
  cursor: pointer;
  background-position: left bottom;
}

.item h2,
.menu h2 {
  margin-top: 5px;
  font-size: 1.5rem;
  font-weight: 500;
}

.item p {
  font-weight: 300;
  font-size: 1.25rem;
  color: $secondary-color;
  margin-bottom: 15px;
}

.selected {
  height: 79vh;
  background: $main-color;
  color: $tint-color;
  justify-content: center;
}
.selected h2 {
  margin-top: 25px;
}

.item a {
  text-decoration: none;
  color: $tint-color;
  transition: all 300ms ease-in-out;
  height: 100%;
}

.highlight-gif {
  width: 100%;
  position: relative;
}

.hidden {
  opacity: 0;
}

.lower-content {
  bottom: 20vh;
}

h1 {
  background: $tint-color;
  margin-top: -200px;
  z-index: 100;
}

#portfolio,
#blog,
#music,
#youtube {
  background-repeat: no-repeat !important;
  background-size: cover;
  background-position: center !important;
  transition: all 300ms linear;
}

#portfolio {
  background: -webkit-linear-gradient($off-black, $off-black),
    url("assets/portfolio.gif");
}

#blog {
  background: -webkit-linear-gradient($off-black, $off-black),
    url("assets/blog.gif");
  background-position: left !important;
}

#youtube {
  background: -webkit-linear-gradient($off-black, $off-black),
    url("assets/youtube.gif");
  background-size: cover !important;
}

#music {
