    url("assets/music.gif");
}

#portfolio:hover,
#blog:hover,
#music:hover,
#youtube:hover {
  opacity: 0.8;
}

.link-text {
  background: $main-color;
  border-radius: 5px;
  box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1),
    0 2px 4px -1px rgba(0, 0, 0, 0.06);
  padding: 10px;
  display: flex;
  justify-content: center;
  flex-direction: column;
  transition: 300ms all linear;
  width: 80%;
}

.link-text:hover {
  box-shadow: 0 20px 25px -5px rgba(0, 0, 0, 0.1),
    0 10px 10px -5px rgba(0, 0, 0, 0.04);
  transform: translateY(-10px);
}

.hide {
  display: none;
}

.visit-btn,
.visit-btn a {
  background: $tint-color;
  text-decoration: none;
  color: $main-color;
  border-radius: 5px;
  margin-top: 10px;
  padding: 5px;
  font-size: 1.2rem;
  transition: all 200ms ease-in-out;
}

.visit-btn:hover,
.visit-btn a:hover {
  cursor: pointer;
  background: $main-color;
  color: $tint-color;
  transform: translateY(5px);
}

@media only screen and (max-width: 800px) {
  .right,
  .left {
    width: 50vw;
  }

  #youtube {
    background: -webkit-linear-gradient($off-black, $off-black),
      url("assets/youtube.gif");
    background-size: cover !important;
  }
}

@media only screen and (max-width: 700px) {
  .container {
    display: flex;
    flex-direction: column-reverse;
  }

  .left {
    width: 100vw;
  }

  .right {
    width: 100vw;
  }

  .scroll-down {
    display: block;
    background: $secondary-color;
    color: $off-white;
    display: flex;
    place-items: center;
    justify-content: center;
    font-size: 1rem;
    position: absolute;
    bottom: 0;
    left: 0;
    padding: 5px 0;
    width: 100%;
    text-align: center;
    opacity: 1;
    animation: fade 1.5s linear infinite;
  }

  #profile-pic {
    margin-top: -10px;
    margin-bottom: -10px;
    height: 40vh;
  }
}

@keyframes fade {
  0%,
  100% {
    opacity: 0.4;
    transform: translateY(-5px);
    color: $off-white;
  }
  50% {
    opacity: 1;
    transform: translateY(0px);
    color: $tint-color;
  }
}
